std::string GetDisplayPCIDeviceName(
    const std::string& vendorId, const std::string& deviceId, const std::string& subdeviceVendorId, std::string& subdeviceId
)
{
    static const std::string kHexPrefix     = "0x";
    static const std::string kIntelId       = "0x8086";
    static const std::string kNvidiaId      = "0x10de";
    static const std::string kAtiId         = "0x1002";
    static const std::string kVmwareId      = "0x15ad";
    static const std::string kZhaoXinId      = "0x1d17";
    static const std::string kIntelName     = "[Intel] ";
    static const std::string kNvidiaName    = "[NVIDIA] ";
    static const std::string kAtiName       = "[AMD/ATI] ";
    static const std::string kVmwareName    = "[VMware] ";
    static const std::string kZhaoXinName    = "[ZhaoXin] ";
    auto                     subdeviceIndex = Replace(subdeviceVendorId, kHexPrefix, "") + Replace(subdeviceId, kHexPrefix, "");
    const PCITable*          table          = nullptr;
    std::string              vendorName     = "[Unknown] ";
    if (kAtiId == vendorId)
    {
        table      = &GetATIDisplayDeviceTable();
        vendorName = kAtiName;
    }
    else if (kIntelId == vendorId)
    {
        table      = &GetINTELDisplayDeviceTable();
        vendorName = kIntelName;
    }
    else if (kNvidiaId == vendorId)
    {
        table      = &GetNVIDIADisplayDeviceTable();
        vendorName = kNvidiaName;
    }
    else if (kVmwareId == vendorId)
    {
        table      = &GetVmWareDisplayDeviceTable();
        vendorName = kVmwareName;
    }
    else if (kZhaoXinId == vendorId)
    {
        table      = &GetZhaoXinDisplayDeviceTable();
        vendorName = kZhaoXinName;
    }
    if (table)
    {
        if (auto iter = table->find(deviceId); iter != table->end())
        {
            const auto& deviceSet = iter->second;
            for (const auto& device : deviceSet)
            {
                if (device.first == subdeviceIndex)
                {
                    return vendorName + device.second;
                }
            }
            return vendorName + deviceSet.at(0).second;
        }
    }
    return vendorName + "Display Adapter";
}
